mapper.select:
SELECT id
FROM m_attribute
WHERE name = #{attributeName}
  AND clrsize = #{clr}

{call m_product_ac(
		#{id, mode=IN, jdbcType=INTEGER},
		#{code, mode=OUT, jdbcType=INTEGER},
		#{message, mode=OUT, jdbcType=VARCHAR}
	)}

mapper.insert:
UPDATE m_product t
SET t.isactive = 'Y'

	,t.m_dim2_id = #{yearId}


	,t.m_dim3_id = #{seasonId}


	,t.m_dim4_id = #{bigClassId}


	,t.m_dim5_id = #{smallClassId}


	,t.m_dim6_id = #{materialBigClassId}


	,t.m_dim7_id = #{HeelTypeId}


	,t.m_dim8_id = #{originPlaceId}


	,t.m_dim9_id = #{qualityGradeId}


	,t.m_dim10_id = #{standardId}



	,t.c_supplier_id = #{supplierId}


	,t.pricelist = #{priceList}


	,t.precost = #{preCost}

WHERE t.id = #{id}

MERGE INTO m_product_alias a USING (
SELECT #{sku.sku} AS sku, #{sku.productId} AS productId, a.id AS asiId, #{sku.forCode, javaType=String, jdbcType=VARCHAR} AS forCode, #{sku.intsCode, javaType=String, jdbcType=VARCHAR} AS intsCode FROM m_attributesetinstance a, m_attributeset s, m_attributevalue c, m_attributevalue c2 WHERE a.m_attributeset_id = s.id AND a.value1_id = c.id AND a.value2_id = c2.id AND s.m_sizegroup_id = #{sku.sizeGroupId} AND c.VALUE = #{sku.colorCode} AND c2.VALUE = #{sku.sizeCode}
AND c.m_dim1_id = #{sku.brandId, javaType=Long, jdbcType=BIGINT}
) b ON (a.m_product_id = b.productId AND a.m_attributesetinstance_id = b.asiId) WHEN NOT MATCHED THEN INSERT (id, ad_client_id, ad_org_id, ownerid, modifierid, creationdate, modifieddate, isactive, no, m_product_id, m_attributesetinstance_id, forcode, intscode) VALUES (get_sequences('m_product_alias'), 37, 27, ${userId}, ${userId}, SYSDATE, SYSDATE, 'Y', b.sku, b.productId, b.asiId, b.forCode, b.intsCode) WHEN MATCHED THEN UPDATE SET a.no = b.sku, a.intscode = b.intsCode, a.modifieddate = SYSDATE

mapper.select:
SELECT t.id, t.name AS code, t.value AS name, t.clrsize AS clr
FROM m_attributevalue t
WHERE t.clrsize = #{clr}
  AND t.value = #{code}
  AND t.m_attribute_id = #{attributeId, javaType=Long, jdbcType=BIGINT}

	AND t.m_dim1_id = #{brandId, javaType=Long, jdbcType=BIGINT}

select t.id
from M_ATTRIBUTESETINSTANCE t , M_ATTRIBUTESET ma
where t.M_ATTRIBUTESET_ID = ma.ID
and ma.M_SIZEGROUP_ID = #{sizeGroupId ,javaType=Long,jdbcType=BIGINT}
and t.VALUE1_CODE = #{colorCode ,javaType=String ,jdbcType=VARCHAR}
and t.VALUE2_CODE = #{sizeCode , javaType=String ,jdbcType=VARCHAR}

SELECT t.id, t.name AS code, t.value AS name, t.clrsize AS clr
FROM m_attributevalue t
WHERE t.clrsize = #{clr}
AND t.NAME = #{attributeValueName}
AND t.m_attribute_id = #{attributeId, javaType=Long, jdbcType=BIGINT}

	AND t.m_dim1_id = #{brandId, javaType=Long, jdbcType=BIGINT}
