mapper.select:
SELECT t.id, t.c_supplier_id AS supplierId, t.c_store_id AS storeId
FROM b_po t
WHERE t.docno = #{poDocno, javaType=String, jdbcType=VARCHAR}

{call m_purchaseitem_am(
        #{itemId, mode=IN, jdbcType=BIGINT}
    )}

{call m_purchase_am(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_purchase_submit(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_purchase_unsubmit(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_purchase_qtycop(
        #{id, mode=IN, jdbcType=BIGINT},
        #{userId, mode=IN, jdbcType=INTEGER},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_purchasein_submit(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

SELECT t.id, t.sourceno, t.datein AS inDate, s.src_customercode AS customerCode FROM m_purchase t, c_store s WHERE t.c_store_id = s.id AND t.id IN (SELECT w.id FROM (SELECT r.id, row_number() over (ORDER BY r.id) AS rn FROM m_purchase r, c_store rs, m_dim m1 WHERE r.c_supplier_id = 1 AND r.c_store_id = rs.id AND rs.m_dim1_id = m1.id AND m1.dimflag = 'DIM1' AND m1.attribcode = 'NE' /*AND rs.src_code IS NOT NULL*/ AND r.in_status = 2 AND r.sourceno IS NOT NULL
AND r.docno = #{docNo}
AND r.toscm_status = 'N'
) w WHERE w.rn BETWEEN #{beg} AND #{end})

mapper.update:
UPDATE m_purchase t
SET t.toscm_status  = #{status, javaType=String, jdbcType=VARCHAR},
    t.toscm_message = #{message, javaType=String, jdbcType=VARCHAR}
WHERE t.id = #{purchaseId, javaType=Long, jdbcType=BIGINT}

mapper.select:
SELECT id, out_status AS outStatus
FROM m_ret_pur
WHERE sourceno = #{sourceNo, javaType=String, jdbcType=VARCHAR}

{call m_ret_puritem_acm(
        #{itemId, mode=IN, jdbcType=BIGINT}
    )}

{call m_ret_pur_am(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_ret_pur_submit(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_ret_pur_unsubmit(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

mapper.update:
UPDATE m_ret_puritem t
SET t.qtyout         = #{qtyOut},
    t.tot_amt_actual = t.priceactual * #{qtyOut},
    t.tot_amt_list   = t.pricelist * #{qtyOut}
WHERE t.m_ret_pur_id = #{mainId}
  AND t.m_productalias_id = #{skuId}

mapper.select:
{call m_ret_purout_qtycop(
        #{id, mode=IN, jdbcType=BIGINT},
        #{userId, mode=IN, jdbcType=INTEGER},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

{call m_ret_purout_submit(
        #{id, mode=IN, jdbcType=BIGINT},
        #{code, mode=OUT, jdbcType=INTEGER},
        #{message, mode=OUT, jdbcType=VARCHAR}
    )}

SELECT t.id, t.docno, t.billdate, s.code AS storeCode, s.name AS storeName, p.code AS supplierCode, p.name AS supplierName, t.dateout AS outDate, t.outtime, t.description, i.id AS itemId, a.no AS sku, i.qty, i.qtyout, i.priceactual FROM m_ret_pur t, c_store s, c_supplier p, m_ret_puritem i, m_product_alias a, m_product pdt, m_dim m18 WHERE t.c_store_id = s.id AND t.c_supplier_id = p.id AND t.id = i.m_ret_pur_id AND i.m_productalias_id = a.id AND i.m_product_id = pdt.id AND pdt.m_dim18_id = m18.id AND m18.attribcode <> '0777' AND t.id IN (SELECT w.id FROM (SELECT r.id, row_number() over (ORDER BY r.id) AS rn FROM m_ret_pur r, m_ret_puritem ri, c_store rs, m_dim m1, m_product rp, m_dim rm18 WHERE r.c_store_id = rs.id AND rs.m_dim1_id = m1.id AND m1.dimflag = 'DIM1' AND m1.attribcode = 'B' AND r.sourceno IS NULL AND r.status = 2 AND r.out_status = 1 AND r.id = ri.m_ret_pur_id AND ri.m_product_id = rp.id AND rp.m_dim18_id = rm18.id AND rm18.attribcode <> '0777'
AND r.docno = #{docNo}
AND r.to_scm_status = 'N'
) w WHERE w.rn BETWEEN #{beg} AND #{end})

mapper.update:
UPDATE m_ret_pur t
SET t.to_scm_status  = #{status, javaType=String, jdbcType=VARCHAR},
    t.to_scm_message = #{message, javaType=String, jdbcType=VARCHAR}

        , t.sourceno = #{bsijaNo}

WHERE t.id = #{purchaseReturnId, javaType=Long, jdbcType=BIGINT}

mapper.select:
SELECT t.id, t.docno, t.billdate, s.code AS storeCode, s.name AS storeName, p.code AS supplierCode, p.name AS supplierName, t.sourceno, t.dateout AS outDate, t.outtime, t.description, i.id AS itemId, a.no AS sku, i.qty, i.qtyout, i.priceactual FROM m_ret_pur t, c_store s, c_supplier p, m_ret_puritem i, m_product_alias a, m_product pdt, m_dim m18 WHERE t.c_store_id = s.id AND t.c_supplier_id = p.id AND t.id = i.m_ret_pur_id AND i.m_productalias_id = a.id AND i.m_product_id = pdt.id AND pdt.m_dim18_id = m18.id AND m18.attribcode <> '0777' AND t.id IN (SELECT w.id FROM (SELECT r.id, row_number() over (ORDER BY r.id) AS rn FROM m_ret_pur r, m_ret_puritem ri, c_store rs, m_dim m1, m_product rp, m_dim rm18 WHERE r.c_store_id = rs.id AND rs.m_dim1_id = m1.id AND m1.dimflag = 'DIM1' AND m1.attribcode = 'B' AND r.sourceno IS NOT NULL AND r.out_status = 2 AND r.id = ri.m_ret_pur_id AND ri.m_product_id = rp.id AND rp.m_dim18_id = rm18.id AND rm18.attribcode <> '0777'
AND r.docno = #{docNo}
AND r.to_scmout_status = 'N'
) w WHERE w.rn BETWEEN #{beg} AND #{end})

mapper.update:
UPDATE m_ret_pur t
SET t.to_scmout_status  = #{status, javaType=String, jdbcType=VARCHAR},
    t.to_scmout_message = #{message, javaType=String, jdbcType=VARCHAR}
WHERE t.id = #{purchaseReturnId, javaType=Long, jdbcType=BIGINT}

mapper.select:
SELECT t.id
FROM m_ret_pur_order t
WHERE t.docno = #{purchaseReturnOrderNo}

SELECT t.id, t.docno, t.billdate, s.name AS storeName, sp.name AS supplierName, t.description, i.id AS itemId, a.no AS sku, i.qty, i.priceactual FROM m_ret_pur_order t, m_ret_pur_orderitem i, c_store s, c_supplier sp, m_product_alias a, m_product p, m_dim m18 WHERE t.c_store_id = s.id AND t.c_supplier_id = sp.id AND t.id = i.m_ret_pur_order_id AND i.m_productalias_id = a.id AND i.m_product_id = p.id AND p.m_dim18_id = m18.id AND m18.attribcode <> '0777' AND t.id IN (SELECT w.id FROM (SELECT r.id, row_number() over (ORDER BY r.id) AS rn FROM m_ret_pur_order r, m_ret_pur_orderitem ri, c_store rs, m_dim m1, m_product rp, m_dim rm18 WHERE r.c_store_id = rs.id AND rs.m_dim1_id = m1.id AND m1.dimflag = 'DIM1' AND m1.attribcode = 'B' AND rs.is_to_scm = 'Y' AND r.status = 2 AND r.id = ri.m_ret_pur_order_id AND ri.m_product_id = rp.id AND rp.m_dim18_id = rm18.id AND rm18.attribcode <> '0777'
AND r.docno = #{docNo}
AND r.to_scm_status = 'N'
) w WHERE w.rn BETWEEN #{beg} AND #{end})

mapper.update:
UPDATE m_ret_pur_order t
SET t.to_scm_status  = #{status, javaType=String, jdbcType=VARCHAR},
    t.scm_no         = #{bsijaNo, javaType=String, jdbcType=VARCHAR},
    t.to_scm_message = #{message, javaType=String, jdbcType=VARCHAR}
WHERE t.id = #{purchaseReturnOrderId, javaType=Long, jdbcType=BIGINT}
